@charset "UTF-8";

/********* public:notices.less ********/
.notices{list-style:none;margin:0;padding:0}.notices.notices--block .notice{margin-bottom:10px}.notices.notices--floating{margin:0 20px 0 auto;width:300px;max-width:100%;z-index:800}@media (max-width:340px){.notices.notices--floating{margin-right:10px}}.notices.notices--floating .notice{margin-bottom:20px}.notices.notices--scrolling{display:flex;align-items:stretch;overflow:hidden;border-width:1px;border-style:solid;border-top-color:#e0e0e0;border-right-color:#d6d6d6;border-bottom-color:#ccc;border-left-color:#d6d6d6;margin-bottom:10px}.notices.notices--scrolling.notices--isMulti{margin-bottom:30px}.notices.notices--scrolling .notice{width:100%;flex-grow:0;flex-shrink:0;border:none}.notice{position:relative;border-width:1px;border-style:solid;border-top-color:#e0e0e0;border-right-color:#d6d6d6;border-bottom-color:#ccc;border-left-color:#d6d6d6}.notice:before,.notice:after{content:" ";display:table}.notice:after{clear:both}.notice.notice--primary{color:#0b0a07;background:#e9e8e6}.notice.notice--accent{color:#aa4909;background:#fef4e7}.notice.notice--accent a:not(.button--notice){color:#ffc02e}.notice.notice--dark{color:#fefefe;border:none;background:#141414}.notice.notice--dark a:not(.button--notice){color:#b4b4b4}.notice.notice--light{color:#141414;background:#fefefe}.notice.notice--light a:not(.button--notice){color:#828282}.notice.notice--warn{color:#fff;border:none;background:#ff3333}.notice.notice--warn a:not(.button--notice){color:#fff}.notice.notice--enablePush{display:none}@media (max-width:900px){.notice.notice--enablePush{padding:3px 3px 10px;font-size:13px}}@media (max-width:900px){.notice.notice--cookie .notice-content{padding:3px 3px 10px;font-size:12px}.notice.notice--cookie .notice-content .button--notice{font-size:12px;padding:3px 6px}.notice.notice--cookie .notice-content .button--notice .button-text{font-size:12px}}.notice.notice--cookieAdvanced .notice-content{padding:1em 0}.notice.notice--cookieAdvanced .notice-content .u-pageCentered{display:grid;grid-template-columns:1fr 250px;gap:10px;align-items:end}@media (max-width:650px){.notice.notice--cookieAdvanced .notice-content .u-pageCentered{display:block}}.notice.notice--cookieAdvanced .notice-content h2{margin:0}.notice.notice--cookieAdvanced .notice-content .button{width:100%;margin:.5em 0;font-weight:bold}.notice.notice--cookieAdvanced .notice-content form.is-active{display:contents;grid-column:span 2}.notice.notice--cookieAdvanced .notice-content form.is-active .formRow.formRow--fullWidth>dd{padding-left:0;padding-right:0}.notice.notice--cookieAdvanced .notice-content form:not(.is-active){display:none}.notices--block .notice{font-size:15px;border-radius:4px}.notices--floating .notice{font-size:11px;border-radius:4px;box-shadow:1px 1px 3px rgba(0,0,0,0.25)}.notices--floating .notice.notice--primary{background-color:rgba(233,232,230,0.8)}.notices--floating .notice.notice--accent{background-color:rgba(254,244,231,0.8)}.notices--floating .notice.notice--dark{background-color:rgba(20,20,20,0.8)}.notices--floating .notice.notice--light{background-color:rgba(254,254,254,0.8)}.has-js .notices--floating .notice{display:none}.notice.notice--hasImage .notice-content{margin-left:68px;min-height:68px}@media (max-width:900px){.notice.notice--hidewide:not(.is-vis-processed){display:none;visibility:hidden}}@media (max-width:650px){.notice.notice--hidemedium:not(.is-vis-processed){display:none;visibility:hidden}}@media (max-width:480px){.notice.notice--hidenarrow:not(.is-vis-processed){display:none;visibility:hidden}}.notice-image{float:left;padding:25px 0 25px 25px}.notice-image img{max-width:48px;max-height:48px}.notice-content{padding:10px}.notice-content a.notice-dismiss{float:right;color:inherit;font-size:16px;line-height:1;height:1em;box-sizing:content-box;padding:0 0 5px 5px;opacity:.5;-webkit-transition: opacity .25s ease;transition: opacity .25s ease;cursor:pointer}.notice-content a.notice-dismiss:before{display:inline-block;content:"";height:1em;vertical-align:-0.125em;background-color:currentColor;width:.75em;mask:url('/styles/fa/regular/times.svg?v=5.15.3') no-repeat center;-webkit-mask:url('/styles/fa/regular/times.svg?v=5.15.3') no-repeat center}.notice-content a.notice-dismiss:hover{text-decoration:none;opacity:1}.notices--floating .notice-content a.notice-dismiss{font-size:14px}