@charset "UTF-8";

/********* public:hypixel_news_index.less ********/
.p-pre-body{padding-top:0}.p-body-main .bbWrapper{font-family:Georgia,serif}.featuredItems{box-sizing:border-box;height:320px;margin:-2px 0 -12px 0}.featuredItems .featuredItems-item{height:100%;width:100%;padding:20px 500px 0 40px;background-position:right center;background-size:contain;background-repeat:no-repeat}.featuredItems .featuredItems-item h1{margin:20px 0 0 0;font-size:34px}.featuredItems .featuredItems-item h2{margin-top:0;font-family:Neuton,serif;font-size:19px;font-weight:300;color:#e2bb60}.featuredItems .featuredItems-item .featuredItems-item-text{font-family:Neuton,serif;font-size:19px;line-height:1.5em}.featuredItems .featuredItems-item .featuredItems-item-cta a{display:block;width:217px;height:54px;margin-top:34px;background:url('/styles/hypixel-v2/images/bg-featured-item-button.png') no-repeat;font-family:Neuton,serif;font-size:19px;font-weight:400;color:#000;line-height:50px;text-align:center;transition:opacity .2s}.featuredItems .featuredItems-item .featuredItems-item-cta a:hover{text-decoration:none;opacity:.8}.upgradeAccountCta{position:relative;width:250px;height:233px;border-radius:5px;background:url('/styles/hypixel-v2/images/upgrade-account-cta.png') no-repeat 0 0;background-size:contain;overflow:hidden}.upgradeAccountCta-body{position:absolute;right:0;left:0;bottom:0;padding:8px 10px 17px 10px;background:rgba(0,0,0,0.5);text-align:center}.upgradeAccountCta-body h3{margin:0;font-family:Neuton,serif;font-size:21px;color:#f4c75d}.upgradeAccountCta-body h4{margin:0 0 10px 0;font-family:Neuton,serif;font-weight:400;font-size:16px;color:#fff}.upgradeAccountCta-body .button{font-family:Neuton;font-size:16px;text-transform:uppercase}@media (max-width:1200px){.featuredItems{display:none !important}}